  var agencytableId = $(this).data('table');         $(".modal-body-agency #agencytableId").val( agencytableId );         $.ajax({            url: "{{ url('admin/getCityModelRecord') }}",            type: "GET",            success: function(data) {                $.each(data.city, function(key, value) {                    $('#agency_city_sel').append($("<option></option>").attr("value",key).text(value));                });            }        });    });    $("#model-agency-form-submit").click(function() {        var agencytableId = $(".modal-body-agency #agencytableId").val();        var ANAME    = $('#AgencyANAME').val();        var DOCODE      = 1;        var AGCODE     = $('#AgencyAGCODE').val();                if(ANAME == '') {            alert('Agency Name Empty Not Allowed');            return false;        } else if(DOCODE == '') {            alert('DO Name Empty Not Allowed');            return false;        } else if(AGCODE == '') {            alert('Agency Code Empty Not Allowed');            return false;        } else {            var data = { "_token": "{{ csrf_token() }}",type : agencytableId,ANAME:ANAME,DOCODE:DOCODE,AGCODE:AGCODE};        }                $.ajax({            url: "{{ url('admin/addModelRecord') }}",            type: "POST",            data: data,            success: function(data) {                $('#agency_sel').append($("<option></option>").attr({value:data.data.AFILE,selected:"selected"}).text(data.data.ANAME));                $(".modal-body-agency").find("input,textarea,select").val('').end();                $('#exampleAgencyModalLong').modal('toggle');            }        });    });</script>