.modal-body-bank #banktableId").val( banktableId );         $.ajax({            url: "{{ url('admin/getCityModelRecord') }}",            type: "GET",            success: function(data) {                $.each(data.city, function(key, value) {                    $('#bank_city_sel').append($("<option></option>").attr("value",key).text(value));                });            }        });    });    $("#model-bank-form-submit").click(function() {        var banktableId = $(".modal-body-bank #banktableId").val();        var bankName    = $('#bankName').val();        var bankCode    = $('#bankCode').val();        var bankMICR    = $('#bankMICR').val();        var bankIFC     = $('#bankIFC').val();        var bankADD1    = $('#bankADD1').val();        var bankADD2    = $('#bankADD2').val();        var bankADD3    = $('#bankADD3').val();        var cityID      = $('#bank_city_sel option:selected').val();        var bankPIN     = $('#bankPIN').val();        var bankPHONE   = $('#bankPHONE').val();        var bankFAX     = $('#bankFAX').val();        var bankEMAIL   = $('#bankEMAIL').val();                if(bankName == '') {            alert('Bank Name Empty Not Allowed');            return false;        } else if(bankCode == '') {            alert('Bank Name / Code Empty Not Allowed');            return false;        } else if(bankMICR == '') {            alert('MICR Code Empty Not Allowed');            return false;        } else if(bankIFC == '') {            alert('IFC Code Empty Not Allowed');            return false;        } else {            var data = { "_token": "{{ csrf_token() }}",type : banktableId,BANK:bankName,BANKBR:bankCode,BANKMICR:bankMICR,BANKIFS:bankIFC,AD1:bankADD1,AD2:bankADD2,AD3:bankADD3,CITYID:cityID,PIN:bankPIN,PHONE:bankPHONE,FAX:bankFAX,EMAIL:bankEMAIL };        }                $.ajax({            url: "{{ url('admin/addModelRecord') }}",            type: "POST",            data: data,            success: function(data) {                $('#bank_sel').append($("<option></option>").attr({value:data.data.BANKCD,selected:"selected"}).text(data.data.BANK));                $(".modal-body-bank").find("input,textarea,select").val('').end();                $('#exampleBankModalLong').modal('toggle');            }        });    });</script>