branchDiv     = $('#branchDiv').val();        var branchZone    = $('#branchZone').val();                if(branchCode == '') {            alert('Branch Code Empty Not Allowed');            return false;        } else if(branchName == '') {            alert('Branch Name Empty Not Allowed');            return false;        } else if(branchManager == '') {            alert('Branch Manager Empty Not Allowed');            return false;        } else {            var data = { "_token": "{{ csrf_token() }}",type : branchtableId,BRANCH:branchCode,BRANCHNM:branchName,BR_MGR_NM:branchManager,ADDRESS:branchAddress,CITYID:cityID,PIN:branchPin,PHONE_O:branchPhone,DIVISION:branchDiv,ZONE:branchZone,CITY:city };        }                $.ajax({            url: "{{ url('admin/addModelRecord') }}",            type: "POST",            data: data,            success: function(data) {                $('#branch_sel').append($("<option></option>").attr({value:data.data.BCODE,selected:"selected"}).text(data.data.BRANCH));                $(".modal-body-branch").find("input,textarea,select").val('').end();                $('#exampleBranchModalLong').modal('toggle');            }        });    });</script>