).val();        var courierMOBILE  = $('#courierMOBILE').val();                if(courierName == '') {            alert('Courier Name Empty Not Allowed');            return false;        } else if(courierPerson == '') {            alert('Courier Person Empty Not Allowed');            return false;        } else {            var data = { "_token": "{{ csrf_token() }}",type : couriertableId,COURIER_NAME:courierName,COURIER_PERSON:courierPerson,AD1:courierADD1,AD2:courierADD2,AD3:courierADD3,CITYID:cityID,PIN:courierPIN,PHONE:courierPHONE,MOBILE:courierMOBILE };        }                $.ajax({            url: "{{ url('admin/addModelRecord') }}",            type: "POST",            data: data,            success: function(data) {                $('#courier_sel').append($("<option></option>").attr({value:data.data.COURIERID,selected:"selected"}).text(data.data.COURIER_NAME));                $(".modal-body-courier").find("input,textarea,select").val('').end();                $('#exampleCourierModalLong').modal('toggle');            }        });    });</script>