     var DealerMobile      = $('#DealerMobile').val();        var DealerShowMobile  = $('#DealerShowMobile').val();        var DealerStatus      = $('#DealerStatus option:selected').val();        var DealerNextDue     = $('#DealerNextDue').val();        var DealerBill        = $('#DealerBill option:selected').val();        var DealerRemark      = $('#DealerRemark').val();        var DealerBank        = $('#DealerBank').val();        var DealerACNO        = $('#DealerACNO').val();        var DealerLimit       = $('#DealerLimit').val();        var DealerCLimit      = $('#DealerCLimit').val();                if(FranchiseeName == '') {            alert('Franchisee Name Empty Not Allowed');            return false;        } else {            var data = { "_token": "{{ csrf_token() }}",type : dealertableId,dealer:DealerName,sortname:DealerSortName,franchid:DealerFranchisee,add1:DealerADD1,add2:DealerADD2,add3:DealerADD3,cityid:cityID,pin:DealerPIN,phone_o:DealerPHONEO,phone_r:DealerPHONER,mobile:DealerMobile,show_mob:DealerShowMobile,dlr_status:DealerStatus,dlr_due:DealerNextDue,bill:DealerBill,remark:DealerRemark,bankname:DealerBank,acno:DealerACNO,r_limit:DealerLimit,c_limit:DealerCLimit };        }                $.ajax({            url: "{{ url('admin/addModelRecord') }}",            type: "POST",            data: data,            success: function(data) {                $('#dealer_sel').append($("<option></option>").attr({value:data.data.dealerid,selected:"selected"}).text(data.data.dealer));                $(".modal-body-dealer").find("input,textarea,select").val('').end();                $('#exampleDealerModalLong').modal('toggle');            }        });    });</script>