bleId );         $.ajax({            url: "{{ url('admin/getCityModelRecord') }}",            type: "GET",            success: function(data) {                $.each(data.city, function(key, value) {                    $('#family_city_sel').append($("<option></option>").attr("value",key).text(value));                });                $.each(data.area, function(key, value) {                    $('#family_area_sel').append($("<option></option>").attr("value",key).text(value));                });            }        });    });    $("#model-family-form-submit").click(function() {        var familytableId   = $(".modal-body-family #familytableId").val();        var family          = $('#family').val();        var family_add1     = $('#family_add1').val();        var family_add2     = $('#family_add2').val();        var family_add3     = $('#family_add3').val();        var family_city_sel = $('#family_city_sel option:selected').val();        var family_area_sel = $('#family_area_sel option:selected').val();        var family_pin      = $('#family_pin').val();        var family_phone_o  = $('#family_phone_o').val();        var family_phone_r  = $('#family_phone_r').val();        var family_mobile   = $('#family_mobile').val();        var family_email    = $('#family_email').val();                if(family == '') {            alert('Family Empty Not Allowed');            return false;        } else if(family_email == '') {            alert('Email Empty Not Allowed');            return false;        } else {            var data = { "_token": "{{ csrf_token() }}",type : familytableId,GNM:family,GAD1:family_add1,GAD2:family_add2,GAD3:family_add3,CITYID:family_city_sel,ARECD:family_area_sel,GPIN:family_pin,GPHON_O:family_phone_o,GPHON_R:family_phone_r,GMOBILE:family_mobile,GEMAIL:family_email };        }                $.ajax({            url: "{{ url('admin/addModelRecord') }}",            type: "POST",            data: data,            success: function(data) {                $('#family_group').append($("<option></option>").attr({value:data.data.GCODE,selected:"selected"}).text(data.data.GNM));                $(".modal-body-family").find("input,textarea,select").val('').end();                $('#exampleFamilyModalLong').modal('toggle');            }        });    });</script>