);        var FranchiseeWorkarea  = $('#FranchiseeWorkarea').val();        var FranchiseeADD1      = $('#FranchiseeADD1').val();        var FranchiseeADD2      = $('#FranchiseeADD2').val();        var FranchiseeADD3      = $('#FranchiseeADD3').val();        var cityID              = $('#franchise_city_sel option:selected').val();        var FranchiseePIN       = $('#FranchiseePIN').val();        var FranchiseePHONEO    = $('#FranchiseePHONEO').val();        var FranchiseePHONER    = $('#FranchiseePHONER').val();        var FranchiseeMobile    = $('#FranchiseeMobile').val();        var FranchiseeEmail     = $('#FranchiseeEmail').val();        var FranchiseeStatus    = $('#FranchiseeStatus').val();        var FranchiseeRemarks   = $('#FranchiseeRemarks').val();                if(FranchiseeName == '') {            alert('Franchisee Name Empty Not Allowed');            return false;        } else {            var data = { "_token": "{{ csrf_token() }}",type : franchisetableId,franchnm:FranchiseeName,sortname:FranchiseeSortName,contactper:FranchiseePerson,workarea:FranchiseeWorkarea,add1:FranchiseeADD1,add2:FranchiseeADD2,add3:FranchiseeADD3,cityid:cityID,pin:FranchiseePIN,phone_o:FranchiseePHONEO,phone_r:FranchiseePHONER,mobile:FranchiseeMobile,email:FranchiseeEmail,fr_status:FranchiseeStatus,remark:FranchiseeRemarks };        }                $.ajax({            url: "{{ url('admin/addModelRecord') }}",            type: "POST",            data: data,            success: function(data) {                $('#franchise_sel').append($("<option></option>").attr({value:data.data.franchid,selected:"selected"}).text(data.data.franchnm));                $(".modal-body-franchise").find("input,textarea,select").val('').end();                $('#exampleFranchiseModalLong').modal('toggle');            }        });    });</script>