    $(document).on("click", ".input-group-addon", function () {         var tableId = $(this).data('table');         var dataModelId = $(this).data('model-id');         $(".modal-body #dataModelId").val( dataModelId );         var dataInsideId = $(this).data('inside-id');         $(".modal-body #dataInsideId").val( dataInsideId );                  $(".district-form").hide();         $(".state-form").hide();         $(".country-form").hide();         $(".area-form").hide();         $(".do-form").hide();         $(".relation-form").hide();         $(".product-form").hide();         $(".modal-body #tableId").val( tableId );         if(tableId == 'rela') {            tableId = 'relation';         }         $(".add-new-title").html("Add "+tableId);         $('.add-new-title').css('textTransform', 'capitalize');                  $('.'+tableId+'-form').show();    });    $("#model-form-submit").click(function() {        var tableId = $(".modal-body #tableId").val();        if(tableId == 'district') {            var dis = $('#districtdata').val();            if(dis == '') {                alert('Empty Not Allowed');                return false;            } else {                var data = { "_token": "{{ csrf_token() }}",type : tableId,DISTRICT:dis };            }        } else if(tableId == 'state') {            var state = $('#statedata').val();            if(state == '') {                alert('Empty Not Allowed');                return false;            } else {                var data = { "_token": "{{ csrf_token() }}",type : tableId,STATE:state };            }        } else if(tableId == 'country') {            var country    = $('#countrydata').val();            var countryisd = $('#countrydataisd').val();            if(country == '') {                alert('Empty Not Allowed');                return false;            } else {                var data = { "_token": "{{ csrf_token() }}",type : tableId,COUNTRY:country,ISD:countryisd };            }        } else if(tableId == 'area') {            var area = $('#areadata').val();            if(area == '') {                alert('Empty Not Allowed');                return false;            } else {                var data = { "_token": "{{ csrf_token() }}",type : tableId,ARE1:area };            }        } else if(tableId == 'do') {            var doDOC = $('#doDOC').val();            var doDON = $('#doDON').val();            var doASM = $('#doASM').val();            if(doDOC == '') {                alert('Developement Officer Code Empty Not Allowed');                return false;            } else if(doDON == '') {                alert('Developement Officer Name Empty Not Allowed');                return false;            } else if(doASM == '') {                alert('Appraisal Start Month Empty Not Allowed');                return false;            } else {                var data = { "_token": "{{ csrf_token() }}",type : tableId,DO_CODE:doDOC,DONAME:doDON,APP_MONTH:doASM };            }        } else if(tableId == 'relation' || tableId == 'rela') {            var relation = $('#relationdata').val();            if(relation == '') {                alert('Empty Not Allowed');                return false;            } else {                var data = { "_token": "{{ csrf_token() }}",type : "rela",RELA:relation };            }        } else if(tableId == 'product') {            var productName = $('#productdata').val();            var productPath = $('#productpath').val();            if(productName == '') {                alert('Product Name Empty Not Allowed');                return false;            } else {                var data = { "_token": "{{ csrf_token() }}",type : tableId,productname:productName,productpath:productPath };            }        }                $.ajax({            url: "{{ url('admin/addModelRecord') }}",            type: "POST",            data: data,            success: function(data) {                if(tableId == 'district') {                    $('#district_sel').append($("<option></option>").attr({value:data.data.DISTRICTID,selected:"selected"}).text(data.data.DISTRICT));                } else if (tableId == 'state') {                    $('#state_sel').append($("<option></option>").attr({value:data.data.STATEID,selected:"selected"}).text(data.data.STATE));                } else if (tableId == 'country') {                    $('#country_sel').append($("<option></option>").attr({value:data.data.COUNTRYID,selected:"selected"}).text(data.data.COUNTRY));                } else if (tableId == 'area') {                    var dataModelId  = $(".modal-body #dataModelId").val();                    var dataInsideId = $(".modal-body #dataInsideId").val();                    if(dataModelId != '') {                        $('.data-model-area-'+dataModelId).append($("<option></option>").attr({value:data.data.ARECD,selected:"selected"}).text(data.data.ARE1));                    } else if(dataInsideId != '') {                        $('#'+dataInsideId+'_area_sel'+dataModelId).append($("<option></option>").attr({value:data.data.ARECD,selected:"selected"}).text(data.data.ARE1));                    } else {                        $('#area_sel').append($("<option></option>").attr({value:data.data.ARECD,selected:"selected"}).text(data.data.ARE1));                    }                } else if (tableId == 'do') {                    $('#do_sel').append($("<option></option>").attr({value:data.data.DOCODE,selected:"selected"}).text(data.data.DONAME));                } else if (tableId == 'relation') {                    $('#relation_with_agent').append($("<option></option>").attr({value:data.data.RELAID,selected:"selected"}).text(data.data.RELA));                } else if (tableId == 'rela') {                    $('#relation_sel').append($("<option></option>").attr({value:data.data.RELAID,selected:"selected"}).text(data.data.RELA));                } else if (tableId == 'product') {                    $('#product_sel').append($("<option></option>").attr({value:data.data.productid,selected:"selected"}).text(data.data.productname));                }                $(".modal-body").find("input,textarea,select").val('').end();                $('#exampleModalLong').modal('toggle');            }        });    });</script>