al() != '') {            $.ajax({                url: "{{ url('admin/getSMSDetailsRecord') }}"+"/"+$(this).val(),                type: "GET",                success: function(data) {                    if(data != '') {                        $('#message').val(data.MESSAGE);                    }                }            });        }    });    $("#model-SMS-form-submit").click(function() {        var banktableId = $(".modal-body-bank #banktableId").val();                $.ajax({            url: "{{ url('admin/addModelRecord') }}",            type: "POST",            data: data,            success: function(data) {                $('#bank_sel').append($("<option></option>").attr({value:data.data.BANKCD,selected:"selected"}).text(data.data.BANK));                $(".modal-body-bank").find("input,textarea,select").val('').end();                $('#exampleSMSModalLong').modal('toggle');            }        });    });</script>